# agno/agno.py

class Agno:
    def __init__(self):
        self.plugins = []
        self.name = "Agno Core"

    def greet(self):
        return f"Hello from {self.name}"

    def add_plugin(self, plugin):
        self.plugins.append(plugin)
        print(f"‚úÖ Plugin {plugin.__class__.__name__} added!")

    def run_plugins(self):
        for plugin in self.plugins:
            if hasattr(plugin, 'run'):
                plugin.run()


class ContextPlugin:
    def __init__(self):
        self.context = "Initial context"

    def get_context(self):
        return self.context

    def update_context(self, new_context):
        self.context = new_context


class HistoryPlugin:
    def __init__(self):
        self.history = []

    def add(self, item):
        self.history.append(item)

    def get_history(self):
        return self.history

    def run(self):
        print("üîÅ Running HistoryPlugin")
